mapper.select:
  		SELECT proc_id
		FROM equ_con
		WHERE proc_id = #{procId}

mapper.update:
UPDATE equ_con
SET equ_code=#{equCode}, proc_id=#{procId}, equ_name=#{equName}, equ_model=#{equModel}, equ_stat=#{equStat}, equ_line=#{equLine}, emp_id=#{empId}, equ_check=#{equCheck}, c_temp=#{ctemp}
WHERE equ_code=#{equCode}
