mapper.update:
update mat_req
set bus_id = #{busId} ,mat_id = #{matId}, mat_req_qt = #{matReqQt}, mat_end_date = #{matEndDate} 
where mat_req_id = #{matReqId}

mapper.select:
with lotsum as (select mat_id, sum(mat_qt) as mat_qt_sum from mat_lot group by mat_id) select m.*, l.mat_qt_sum from material m left join lotsum l on m.mat_id = l.mat_id where m.mat_stat = 'Y'
and m.mat_name = #{matName}
order by m.mat_id

mapper.insert:

call  mat_rec_del(
  #{matRecId, mode=IN, jdbcType=VARCHAR},
  #{matLotId, mode=IN, jdbcType=VARCHAR},
  #{outMessage, mode=OUT, jdbcType=VARCHAR}
)

mapper.select:
select l.mat_lot_id, l.mat_id, m.mat_name, m.mat_size, l.mat_qt from mat_lot l left join material m on l.mat_id = m.mat_id
where l.mat_lot_id = #{matLotId}
order by l.mat_lot_id desc

mapper.insert:

call  mat_adj_insert(
  #{matLotId, mode=IN, jdbcType=VARCHAR},
  #{matAdjQt, mode=IN, jdbcType=INTEGER}
)


call  mat_adj_del(
  #{matLotId, mode=IN, jdbcType=VARCHAR},
  #{matAdjQt, mode=IN, jdbcType=INTEGER}
)


call  sp_adj_insert(
  #{spLotId, mode=IN, jdbcType=VARCHAR},
  #{spAdjQt, mode=IN, jdbcType=INTEGER}
)


call  sp_adj_del(
  #{spLotId, mode=IN, jdbcType=VARCHAR},
  #{spAdjQt, mode=IN, jdbcType=INTEGER}
)

mapper.select:
with splotsum as (select sp_id, sum(sp_qt) as sp_qt_sum from sp_lot group by sp_id) select p.*, s.sp_qt_sum from semi_prod p left join splotsum s on p.sp_id = s.sp_id where p.sp_stat = 'Y'
and p.sp_name = #{spName}
order by p.sp_id
