mapper.select:
	{CALL proc_sp_lot_info(
            #{spId, mode=IN, jdbcType=VARCHAR},
            #{spLotId, mode=OUT, jdbcType=VARCHAR},
            #{spQt, mode=OUT, jdbcType=INTEGER}
        )
    }

mapper.update:
UPDATE sp_lot
SET sp_qt=#{spQt}, sp_lot_qt_ck=#{spLotQtCk}
WHERE sp_lot_id=#{spLotId}
